scriptCalls.value:
const tokens = actor.getActiveTokens();
if (state) {
  for (const token of tokens) {
    // Store original values
    token.document.setFlag("pf1", "buffs.alterSelf.size", actor.data.data.traits.size);
    token.document.setFlag("pf1", "buffs.alterSelf.dimSight", token.document.data.dimSight);
    token.document.setFlag("pf1", "buffs.alterSelf.brightSight", token.document.data.brightSight);
    token.document.setFlag("pf1", "buffs.alterSelf.lowLightVision", token.document.getFlag("pf1", "lowLightVision") || false);
    console.log(token.document);
    // Set dark vision
    token.document.update({"brightSight": 60});
    token.document.update({"dimSight": 0});
    token.document.setFlag("pf1", "lowLightVision", true);
  }
  // Set size
  await actor.update({'data.traits.size': 'sm'});
}
else {
  var size;
  for (const token of tokens) {
    // Restore vision
    token.document.setFlag("pf1", "lowLightVision", token.document.getFlag("pf1", "buffs.alterSelf.lowLightVision"));
    token.document.update({"brightSight": parseInt(token.document.getFlag("pf1", "buffs.alterSelf.brightSight"))});
    token.document.update({"dimSight": parseInt(token.document.getFlag("pf1", "buffs.alterSelf.dimSight"))});
    console.log(parseInt(token.document.getFlag("pf1", "buffs.alterSelf.brightSight")));
    size = token.document.getFlag("pf1", "buffs.alterSelf.size");
  };
  // Restore size
  await actor.update({'data.traits.size': size});  
}
